mapper.select:
 		SELECT REPORT_NO,ID,REVIEW_NO,REPORT_TYPE_NO,REPORT_CONTENTS,re_time,reportId as reportId
		FROM(SELECT row_number() over(order by REPORT_NO desc) as re_num, r.REPORT_NO,r.ID,r.REVIEW_NO,r.COMMENTS_NO, r.REPORT_TYPE_NO, r.REPORT_CONTENTS, r.re_time, rv.id as reportId
		FROM(

		) r, review rv where r.REVIEW_NO=rv.REVIEW_NO)
		where re_num between #{startRowNumber} and #{endRowNumber}
		order by report_no desc

 		SELECT REPORT_NO,ID,COMMENTS_NO,REPORT_TYPE_NO,REPORT_CONTENTS,re_time,reportId as reportId
		FROM(SELECT row_number() over(order by r.REPORT_NO desc) as re_num, r.REPORT_NO,r.ID,r.REVIEW_NO,r.COMMENTS_NO, r.REPORT_TYPE_NO, r.REPORT_CONTENTS, r.re_time, c.id as reportId
		FROM(

		) r, COMMENTS c where r.COMMENTS_NO=c.COMMENTS_NO)
		where re_num between #{startRowNumber} and #{endRowNumber}
		order by report_no desc
